import React, { useContext } from 'react';
import { UserContext } from '../UserContext';
import { Link, Navigate, useParams } from 'react-router-dom';

export default function AccountPage() {
    const { user, ready } = useContext(UserContext);
    const { subpage } = useParams(); // Move useParams to the top

    if (!ready) {
        return 'Loading...';
    }

    if (ready && !user) {
        return <Navigate to={'/login'} />;
    }

    // console.log(subpage); // Uncomment if you need to debug

     function LinkClasses(type=null){
      let classes='py-2 px-6';
      if(type===subpage||(subpage==undefined&& type==='profile')){
        classes+='bg-primary text-white rounded-full'
      }
      return classes
     }
    return (
        <div>
            <nav className='w-full flex justify-center mt-8 gap-2'>
                <Link  to={'/account'} className={LinkClasses('profile')}>My Profile</Link>
                <Link to={'/account/bookings'} className={LinkClasses('')}>My Bookings</Link>
                <Link to={'/account/places'} className={LinkClasses('')}>My Accommodations</Link>
            </nav>
        </div>
    );
}
