import Perks from "../Perks";
import { useEffect, useState } from "react";
import axios from "axios";

import { Link, Navigate, useParams } from "react-router-dom";
import PhotosUploader from "../PhotosUploader";

export default function PlacesFormPage() {
    const action=useParams
    const { id } = useParams();

    const [redirectToPlacesList, setRedirectToPlacesList  ] = useState(false);
    const [photoLink, setPhotoLink] = useState('');

    function inputHeader(text) {
        return (
            <h2 className="text-2xl mt-4">{text}</h2>
        );
    }
    function inputDescription(text) {
        return (
            <p className="text-gray-500 text-sm">{text}</p>
        );
    }
    function preInput(header, description) {
        return (
            <>
                {inputHeader(header)}
                {inputDescription(description)}
            </>
        );
    }

    async function savePlace(ev) {
        ev.preventDefault();
        const placeData = {
            title, address, addedPhotos,
            description, perks, extraInfo,
            checkIn, checkOut, maxGuests, price,
        };

    }
    function uploadPhoto(ev) {
        const files = ev.target.files;
        const data = new FormData();
        for (let i = 0; i < files.length; i++) {
          data.append('photos', files[i]);
        }
        axios.post('/upload', data, {
          headers: {'Content-type':'multipart/form-data'}
        }).then(response => {
          const {data:filenames} = response;
          setAddedPhotos(prev => {
            return [...prev, ...filenames];
          });
        })
      }

    async function addPhotoByLink(ev) {
        ev.preventDefault();
        const { data: filenames } = await axios.post('/upload-by-link', { link: photoLink });
        setAddedPhotos(prev => {
            return [...prev, ...filenames];
        });
        setPhotoLink('')
    }
   async function addNewPlace(ev){
        ev.preventDefault()
        const {data}= await axios.post('/places' , {
            title,address,addedPhotos,perks,description,extraInfo,checkIn,checkOut,maxGuests
        })
       setRedirectToPlacesList('/account/places');
    }
    if (redirectToPlacesList && action!=='new') {
        return <Navigate to={'/ account/places '} />
    }

    return (
       <div>
       {action!=='new' && (
        <div className="text-center">
            <Link className="inline-flex gap-1 bg-primary text-white">
            Add a new place
            </Link>
        </div>
       )}
       {action=='new'&&(
        <div>

        </div>
       )}
       </div>
    );
}
