import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
export default function PlacePage(){
    const {id}=useParams()
    const[places,setPlaces]=useState(null);
    useEffect(()=>{
        if(!id){
            return;
        }
        axios.get(`/places/${id}`).then(response=>{
            setPlaces(response.data)
        })
    },[id])
 if(!places) return ''
    return(
        <div className="mt-8 bg-gray-100 -mx-8 px-8 py-4">

            <h1 className="text-xl">{places.title} </h1>
            <a target='_blank' className="my-2  block font-semibold underline" href={'https://maps.google.com/?q='+places.address}>{places.address}</a>
            <div className="grid gap-2 grid-cols-[2fr_1fr]">
                <div>
                 {places.photos?.[0]&&(
                    <img src={'http://localhost:4001/uploads/'+places.photos[0]} alt="" />
                 )}
                </div>
                <div className="grid-gap-2">
                {places.photos?.[0]&&(
                    <img src={'http://localhost:4001/uploads/'+places.photos[1]} alt="" />
                 )}
                  {places.photos?.[0]&&(
                    <img src={'http://localhost:4001/uploads/'+places.photos[3]} alt="" />
                 )}
                </div>
            </div>
        </div>
    )
}
