import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
export default function PlacePage() {
    const { id } = useParams()
    const [places, setPlaces] = useState(null);
    const[showAllPhotos,setsShowAllPhotos]=useState(false) 
    useEffect(() => {
        if (!id) {
            return;
        }
        axios.get(`/places/${id}`).then(response => {
            setPlaces(response.data)
        })
    }, [id])
    if (!places) return ''
    if(showAllPhotos){
        return(
            <div className="fixed bg-white min-w-full min-h-screen">
                {places?.photos?.length}
            </div>
        )
    }
    return (
        <div className="mt-8 bg-gray-100 -mx-8 px-8 py-4">

            <h1 className="text-xl">{places.title} </h1>
            <a target='_blank' className="my-2  block font-semibold underline" href={'https://maps.google.com/?q=' + places.address}>{places.address}</a>
            <div className="relative">
                <div className="grid gap-2 grid-cols-[2fr_1fr]">
                    <div className="">
                        {places.photos?.[0] && (
                            <img className="aspect-square object-cover" src={'http://localhost:4001/uploads/' + places.photos[0]} alt="" />
                        )}
                    </div>
                    <div className="grid">
                        {places.photos?.[0] && (
                            <img className="aspect-square object-cover" src={'http://localhost:4001/uploads/' + places.photos[1]} alt="" />
                        )}
                        <div className="overflow-hidden">
                            {places.photos?.[0] && (
                                <img src={'http://localhost:4001/uploads/' + places.photos[3]} alt="" className='aspect-square object-cover relative top-2' />
                            )}
                        </div>

                    </div>
                </div>
                <button onClick={()=>setsShowAllPhotos(true)} className="flex gap-1 absolute bottom-2 right-2 py-2 px-4 bg-white rounded-2xl  shadow-md shadow-gray-black ">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="size-6">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M6.827 6.175A2.31 2.31 0 0 1 5.186 7.23c-.38.054-.757.112-1.134.175C2.999 7.58 2.25 8.507 2.25 9.574V18a2.25 2.25 0 0 0 2.25 2.25h15A2.25 2.25 0 0 0 21.75 18V9.574c0-1.067-.75-1.994-1.802-2.169a47.865 47.865 0 0 0-1.134-.175 2.31 2.31 0 0 1-1.64-1.055l-.822-1.316a2.192 2.192 0 0 0-1.736-1.039 48.774 48.774 0 0 0-5.232 0 2.192 2.192 0 0 0-1.736 1.039l-.821 1.316Z" />
                        <path strokeLinecap="round" strokeLinejoin="round" d="M16.5 12.75a4.5 4.5 0 1 1-9 0 4.5 4.5 0 0 1 9 0ZM18.75 10.5h.008v.008h-.008V10.5Z" />
                    </svg>

                    Show More Photos</button>
            </div>
        </div>
    )
}
